import React, { useState } from 'react';
import CodeEditor from './CodeEditor';
import api from '../../services/api';

const Question = ({ question, onAnswerChange, currentAnswer }) => {
  const [runOutput, setRunOutput] = useState('');
  const [loading, setLoading] = useState(false);

  const runCode = async (mode) => {
    if (!currentAnswer?.trim()) {
      setRunOutput('Please provide code before running.');
      return;
    }
    try {
      setLoading(true);
      setRunOutput(mode === 'run' ? 'Running code...' : 'Testing code...');

      let res;
      if (mode === 'run') {
        // ✅ Matches backend run_code_view (expects code & input_data)
        res = await api.post('/run-code/', {
          code: currentAnswer,
          input_data: question.sample_input || '',
          language: question.language || 'python', // optional if backend defaults to python
        });
        setRunOutput(res.data.stdout || res.data.output || 'No output returned.');
      } else {
        // ✅ Matches backend test_code_view (expects question_id & code)
        res = await api.post('/test-code/', {
          question_id: question.id,
          code: currentAnswer,
          language: question.language || 'python',
        });

        if (Array.isArray(res.data.results)) {
          const testResults = res.data.results
            .map(
              (r, idx) =>
                `Test case ${idx + 1}:
Input: ${r.input}
Expected: ${r.expected}
Output: ${r.output}
Passed: ${r.passed ? 'Yes' : 'No'}`
            )
            .join('\n-----------------\n');
          setRunOutput(testResults);
        } else if (res.data.error) {
          setRunOutput(`Error: ${res.data.error}`);
        } else {
          setRunOutput('No test results returned.');
        }
      }
    } catch (err) {
      // ✅ More robust error display
      setRunOutput(
        `Error: ${
          err.response?.data?.error ||
          err.response?.data?.detail ||
          err.message
        }`
      );
    } finally {
      setLoading(false);
    }
  };

  const renderMCQ = () => (
    <div className="space-y-4">
      {question.choices?.map((choice) => (
        <label key={choice.id} className="block cursor-pointer">
          <input
            type="radio"
            name={`question-${question.id}`}
            value={choice.choice_text}
            checked={currentAnswer === choice.choice_text}
            onChange={(e) => onAnswerChange(question.id, e.target.value)}
            className="mr-2"
          />
          {choice.choice_text}
        </label>
      ))}
    </div>
  );

  const renderText = () => (
    <input
      type="text"
      className="form-input w-full border border-gray-300 rounded px-3 py-2"
      value={currentAnswer || ''}
      onChange={(e) => onAnswerChange(question.id, e.target.value)}
      placeholder="Type your answer here..."
    />
  );

  const renderTrueFalse = () => (
    <div className="space-y-2">
      {['True', 'False'].map((option) => (
        <label key={option} className="block cursor-pointer">
          <input
            type="radio"
            name={`question-${question.id}`}
            value={option}
            checked={currentAnswer === option}
            onChange={(e) => onAnswerChange(question.id, e.target.value)}
            className="mr-2"
          />
          {option}
        </label>
      ))}
    </div>
  );

  const renderCode = () => (
    <div>
      {(question.sample_input || question.sample_output) && (
        <div className="mb-4 p-3 bg-gray-100 rounded">
          {question.sample_input && (
            <>
              <p className="font-semibold">Sample Input:</p>
              <pre className="bg-white p-2 rounded border">{question.sample_input}</pre>
            </>
          )}
          {question.sample_output && (
            <>
              <p className="font-semibold mt-2">Sample Output:</p>
              <pre className="bg-white p-2 rounded border">{question.sample_output}</pre>
            </>
          )}
        </div>
      )}

      <CodeEditor
        code={currentAnswer || ''}
        onCodeChange={(code) => onAnswerChange(question.id, code)}
      />

      <div className="mt-3 flex gap-3">
        <button
          onClick={() => runCode('run')}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:opacity-50"
          disabled={loading}
          type="button"
        >
          Run
        </button>
        <button
          onClick={() => runCode('test')}
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 disabled:opacity-50"
          disabled={loading}
          type="button"
        >
          Test
        </button>
      </div>

      {runOutput && (
        <div
          className="mt-3 p-3 bg-black text-white rounded whitespace-pre-wrap font-mono"
          aria-live="polite"
        >
          <pre>{runOutput}</pre>
        </div>
      )}
    </div>
  );

  const renderQuestionInput = () => {
    switch (question.question_type) {
      case 'mcq':
        return renderMCQ();
      case 'text':
        return renderText();
      case 'tf':
        return renderTrueFalse();
      case 'code':
        return renderCode();
      default:
        return (
          <p className="text-red-600">
            Unsupported question type: {question.question_type}
          </p>
        );
    }
  };

  return (
    <div className="card mb-6 p-4 border rounded shadow">
      <h3 className="text-xl font-semibold mb-4">{question.question_text}</h3>
      {renderQuestionInput()}
    </div>
  );
};

export default Question;
