import React, { useEffect, useState, useCallback } from 'react';
import { useParams, useSearchParams, useNavigate } from 'react-router-dom';
import Question from '../components/quiz/Question';
import Timer from '../components/quiz/Timer';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { toast } from 'react-toastify';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';

const LOCAL_STORAGE_KEY = 'quiz_answers_draft';

const QuizPage = () => {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const attemptId = searchParams.get('attempt');
  const navigate = useNavigate();
  const { accessToken } = useAuth();

  const [answers, setAnswers] = useState({});
  const [quizDetails, setQuizDetails] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitError, setSubmitError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [alreadySubmitted, setAlreadySubmitted] = useState(false);

  const [currentQIndex, setCurrentQIndex] = useState(0);

  // Load saved answers draft
  useEffect(() => {
    if (attemptId) {
      const saved = localStorage.getItem(`${LOCAL_STORAGE_KEY}_${attemptId}`);
      if (saved) {
        try {
          setAnswers(JSON.parse(saved));
        } catch {}
      }
    }
  }, [attemptId]);

  // Save draft on answers update
  useEffect(() => {
    if (attemptId) {
      localStorage.setItem(`${LOCAL_STORAGE_KEY}_${attemptId}`, JSON.stringify(answers));
    }
  }, [answers, attemptId]);

  useEffect(() => {
    if (!id || !attemptId) {
      setError('Quiz ID or Attempt ID missing from URL.');
      setLoading(false);
      return;
    }
    if (!accessToken) {
      setError('Not authenticated. Please log in.');
      setLoading(false);
      return;
    }

    const fetchAttempt = async () => {
      try {
        const res = await api.get(`/student-quizzes/${attemptId}/`);
        const data = res.data;
        if (data.submitted) setAlreadySubmitted(true);
        setQuizDetails({
          title: data.quiz_title || data.quiz?.title || 'Quiz',
          description: data.quiz_description || data.quiz?.description || '',
          duration_minutes: data.duration_minutes ?? data.quiz?.duration_minutes ?? 60,
        });
        setQuestions(Array.isArray(data.questions) ? data.questions : []);
      } catch (err) {
        const message = err.response?.data?.detail || err.message || 'Failed to load quiz.';
        setError(message);
        toast.error(message);
      } finally {
        setLoading(false);
      }
    };

    fetchAttempt();
  }, [id, attemptId, accessToken]);

  // Handle user answers
  const handleAnswerChange = (questionId, answer) => {
    setAnswers((prev) => ({ ...prev, [String(questionId)]: answer }));
  };

  // Submit answers
  const handleSubmit = useCallback(() => {
    if (alreadySubmitted) {
      toast.info('You have already submitted this quiz.');
      return;
    }
    if (questions.length === 0) {
      setSubmitError('No questions to submit.');
      return;
    }
    // Check answers to submit only non-null answers
    const answersPayload = questions
      .map((q) => {
        const qid = String(q.id);
        const ans = answers[qid] ?? '';
        return {
          question: Number(qid),
          selected_option: typeof ans === 'number' ? ans : Number(ans) || null,
        };
      })
      .filter((a) => a.selected_option !== null && a.selected_option !== '');

    if (answersPayload.length === 0) {
      setSubmitError('Please answer at least one question.');
      return;
    }
    if (!window.confirm('Are you sure you want to submit the quiz?')) return;
    setSubmitError(null);
    setIsSubmitting(true);

    api.post(`/student-quizzes/${attemptId}/submit/`, { answers: answersPayload })
      .then((res) => {
        toast.success('Quiz submitted successfully!');
        setAlreadySubmitted(true);
        localStorage.removeItem(`${LOCAL_STORAGE_KEY}_${attemptId}`);
        navigate(`/results/${attemptId}`, { state: { result: res.data } });
      })
      .catch((err) => {
        const message =
          err.response?.data?.error ||
          err.response?.data?.detail ||
          err.message ||
          'Submission failed. Please try again.';
        setSubmitError(message);
        toast.error(message);
        if (message.includes('already been submitted')) setAlreadySubmitted(true);
      })
      .finally(() => setIsSubmitting(false));
  }, [answers, attemptId, alreadySubmitted, navigate, questions]);

  // Pagination handlers
  const goToNext = () => {
    if (currentQIndex < questions.length - 1) setCurrentQIndex((idx) => idx + 1);
  };

  const goToPrev = () => {
    if (currentQIndex > 0) setCurrentQIndex((idx) => idx - 1);
  };

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  const currentQuestion = questions[currentQIndex];

  return (
    <div className="container mx-auto mt-8 max-w-3xl">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-gray-800">{quizDetails?.title}</h2>
        <Timer
          durationMinutes={quizDetails?.duration_minutes}
          onTimeUp={() => {
            if (!alreadySubmitted) handleSubmit();
          }}
        />
      </div>

      {submitError && (
        <div className="mb-4 p-3 rounded border border-red-600 text-red-700 bg-red-100">
          {submitError}
        </div>
      )}

      {!!currentQuestion ? (
        <Question
          question={currentQuestion}
          onAnswerChange={handleAnswerChange}
          currentAnswer={answers[String(currentQuestion.id)]}
          disabled={alreadySubmitted}
        />
      ) : (
        <p className="text-gray-500">No questions available.</p>
      )}

      <div className="flex justify-between mt-6">
        <button
          onClick={goToPrev}
          disabled={currentQIndex === 0}
          className={`btn px-6 py-2 rounded ${
            currentQIndex === 0
              ? 'bg-gray-300 cursor-not-allowed'
              : 'bg-indigo-600 hover:bg-indigo-700 text-white'
          }`}
        >
          Previous
        </button>

        {currentQIndex < questions.length - 1 ? (
          <button
            onClick={goToNext}
            className="btn px-6 py-2 rounded bg-indigo-600 hover:bg-indigo-700 text-white"
          >
            Next
          </button>
        ) : (
          <button
            onClick={handleSubmit}
            disabled={isSubmitting || alreadySubmitted}
            className={`btn px-6 py-2 rounded ${
              alreadySubmitted
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-green-600 hover:bg-green-700 text-white'
            }`}
          >
            {alreadySubmitted ? 'Submitted' : isSubmitting ? 'Submitting...' : 'Submit Quiz'}
          </button>
        )}
      </div>
    </div>
  );
};

export default QuizPage;
