import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { useAuth } from '../contexts/AuthContext';
import { Link, useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statusMessage, setStatusMessage] = useState(null);
  const { authToken } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuizzes = async () => {
      if (!authToken) {
        setError('No access token found. Please login.');
        setLoading(false);
        return;
      }

      try {
        const response = await axios.get(
          'http://localhost:8000/api/quizzes/',
          {
            headers: {
              Authorization: `Token ${authToken}`,
            },
          }
        );
        setQuizzes(response.data);
      } catch (err) {
        setError('Failed to load quizzes. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchQuizzes();
  }, [authToken]);

  const handleStartQuiz = async (quizId) => {
    if (!authToken) {
      setStatusMessage({ type: 'error', text: 'No access token found. Please login.' });
      return;
    }
    try {
      const response = await axios.post(
        `http://localhost:8000/api/quizzes/${quizId}/start/`,
        {},
        {
          headers: {
            Authorization: `Token ${authToken}`,
          },
        }
      );

      if (response.status === 200) {
        const attemptId = response.data.attempt_id;
        setStatusMessage({ type: 'success', text: 'Quiz started successfully!' });

        setTimeout(() => {
          navigate(`/quiz/${quizId}?attempt=${attemptId}`);
        }, 500);
      } else {
        setStatusMessage({ type: 'error', text: 'Failed to start quiz. Please try again.' });
      }
    } catch (err) {
      const errorMessage =
        err.response?.data?.error || 'Failed to start quiz. Please try again.';
      setStatusMessage({ type: 'error', text: errorMessage });
    }
  };

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Available Quizzes</h2>

      {statusMessage && (
        <div
          className={`mb-4 p-3 rounded ${
            statusMessage.type === 'success'
              ? 'bg-green-100 text-green-700'
              : 'bg-red-100 text-red-700'
          }`}
        >
          {statusMessage.text}
        </div>
      )}

      {quizzes.length === 0 ? (
        <p className="text-gray-600">No quizzes available at the moment.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {quizzes.map((quiz) => (
            <div key={quiz.id} className="card">
              <h3 className="text-xl font-semibold mb-2">
                <Link to={`/quiz/${quiz.id}`} className="text-blue-600 hover:underline">
                  {quiz.title}
                </Link>
              </h3>
              <p className="text-gray-600 mb-4">{quiz.description}</p>
              <div className="flex justify-between items-center text-sm text-gray-500 mb-4">
                <span>Duration: {quiz.duration_minutes} minutes</span>
              </div>
              <button
                onClick={() => handleStartQuiz(quiz.id)}
                className="btn btn-primary w-full"
              >
                Start Quiz
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
