// src/components/auth/SignupForm.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';  // Centralized api instance
import { toast } from 'react-toastify';

const SignupForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.username.trim()) {
      newErrors.username = 'Username is required.';
    }
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required.';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid.';
    }
    if (formData.password.length < 8) {
      newErrors.password = 'Password must be at least 8 characters long.';
    }
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error('Please correct the form errors.');
      return;
    }

    try {
      await api.post('/users/register/', {
        username: formData.username,
        email: formData.email,
        password: formData.password,
      });

      toast.success('Registration successful! Redirecting to login...');
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      console.error('Registration failed:', err.response?.data || err.message);
      // Collect all possible error messages
      const serverErrors = err.response?.data || {};
      let errorMessage = 'Registration failed. Please try again.';
      if (serverErrors.email) errorMessage = serverErrors.email.join(' ');
      else if (serverErrors.username) errorMessage = serverErrors.username.join(' ');
      else if (serverErrors.detail) errorMessage = serverErrors.detail;
      toast.error(errorMessage);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md card p-6 shadow-md bg-white rounded">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">
          Create an account
        </h2>
        <form onSubmit={handleSubmit} noValidate>
          <div className="form-group mb-4">
            <label className="form-label block mb-1 font-semibold" htmlFor="username">Username</label>
            <input
              id="username"
              type="text"
              className={`form-input w-full border px-3 py-2 rounded ${errors.username ? 'border-red-500' : 'border-gray-300'}`}
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              autoComplete="username"
            />
            {errors.username && <p className="text-red-500 text-sm mt-1">{errors.username}</p>}
          </div>
          <div className="form-group mb-4">
            <label className="form-label block mb-1 font-semibold" htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              className={`form-input w-full border px-3 py-2 rounded ${errors.email ? 'border-red-500' : 'border-gray-300'}`}
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              autoComplete="email"
            />
            {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
          </div>
          <div className="form-group mb-4">
            <label className="form-label block mb-1 font-semibold" htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              className={`form-input w-full border px-3 py-2 rounded ${errors.password ? 'border-red-500' : 'border-gray-300'}`}
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              autoComplete="new-password"
            />
            {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
          </div>
          <div className="form-group mb-4">
            <label className="form-label block mb-1 font-semibold" htmlFor="confirmPassword">Confirm Password</label>
            <input
              id="confirmPassword"
              type="password"
              className={`form-input w-full border px-3 py-2 rounded ${errors.confirmPassword ? 'border-red-500' : 'border-gray-300'}`}
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
              autoComplete="new-password"
            />
            {errors.confirmPassword && <p className="text-red-500 text-sm mt-1">{errors.confirmPassword}</p>}
          </div>
          <button
            type="submit"
            className="btn btn-primary w-full py-2 rounded bg-blue-600 hover:bg-blue-700 text-white font-semibold"
          >
            Sign Up
          </button>
        </form>
      </div>
    </div>
  );
};

export default SignupForm;
