// src/services/api.js
import axios from 'axios';

// Base URL is relative — React's proxy in package.json will forward
// Example: "proxy": "http://web:8000"
const api = axios.create({
  baseURL: '/api/', // keep trailing slash to avoid double slashes in requests
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true, // allows cookies/CSRF if backend uses them
});

// Attach token from localStorage (if available) to every request
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Token ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Handle global 401 Unauthorized errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      console.warn('Authentication token expired or invalid. Redirecting to login...');
      // Optionally clear token and redirect
      // localStorage.removeItem('authToken');
      // window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
