import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Header = () => {
  const { user, logout } = useAuth();

  return (
    <header className="bg-indigo-700 text-white shadow-md">
      <div className="container mx-auto flex justify-between items-center py-4 px-6">
        <Link to="/" className="text-2xl font-bold hover:text-indigo-300">
          Quiz Website
        </Link>
        <nav className="space-x-6 text-lg">
          {user ? (
            <>
              <Link to="/dashboard" className="hover:text-indigo-300">Dashboard</Link>
              <Link to="/leaderboard" className="hover:text-indigo-300">Leaderboard</Link>
              <button
                onClick={logout}
                className="hover:text-indigo-300 focus:outline-none"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="hover:text-indigo-300">Login</Link>
              <Link to="/signup" className="hover:text-indigo-300">Sign Up</Link>
            </>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
