// src/components/shared/Watermark.js
import React from 'react';

const Watermark = ({ text }) => (
  <div
    style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      zIndex: 0,
      pointerEvents: 'none',
      opacity: 0.08, // very faint
      fontSize: '2rem',
      color: '#999',
      background: `repeating-linear-gradient(
          0deg,
          transparent 0px,
          transparent 40px,
          rgba(0,0,0,0.05) 40px,
          rgba(0,0,0,0.05) 80px
        )`
    }}
  >
    <div
      style={{
        width: '100vw',
        height: '100vh',
        display: 'flex',
        flexWrap: 'wrap',
        flexDirection: 'column',
        gap: '2rem',
        alignContent: 'center',
        justifyContent: 'center',
      }}
    >
      {[...Array(10)].map((_, i) => (
        <div key={i} style={{ margin: '20px', wordBreak: 'break-all' }}>
          {text}
        </div>
      ))}
    </div>
  </div>
);

export default Watermark;
