import React, { useState, useEffect, useRef } from 'react';

const Timer = ({ durationMinutes, onTimeUp }) => {
  const [timeLeft, setTimeLeft] = useState(durationMinutes * 60);
  const onTimeUpCalled = useRef(false);

  useEffect(() => {
    setTimeLeft(durationMinutes * 60);
    onTimeUpCalled.current = false;
  }, [durationMinutes]);

  useEffect(() => {
    if (timeLeft <= 0) {
      if (!onTimeUpCalled.current) {
        onTimeUpCalled.current = true;
        onTimeUp();
      }
      return;
    }

    const timerId = setInterval(() => {
      setTimeLeft((prevTime) => prevTime - 1);
    }, 1000);

    return () => clearInterval(timerId);
  }, [timeLeft, onTimeUp]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div
      role="timer"
      aria-live="polite"
      className="text-center text-lg font-bold text-gray-800 p-4 rounded-md bg-white shadow-md"
    >
      Time Left: <span className="text-red-500">{formatTime(timeLeft)}</span>
    </div>
  );
};

export default Timer;
