// src/services/api.js
import axios from 'axios';

// Use the backend URL from environment variable (set via .env files or docker-compose)
// Fallback to localhost if not set (e.g. running outside Docker without env file)
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/';

// Create an axios instance with the correct base URL
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true, // send cookies for CSRF/session authentication
});

// Request interceptor to add the authentication token from localStorage
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Token ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor for handling 401 Unauthorized errors globally
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      console.warn('Authentication token expired or invalid. Please log in again.');
      // Optionally clear token and redirect to login page
      // localStorage.removeItem('authToken');
      // window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
