// src/pages/QuizPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import Question from '../components/quiz/Question';
import Timer from '../components/quiz/Timer';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { toast } from 'react-toastify';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';

const QuizPage = () => {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const attemptId = searchParams.get('attempt');
  const navigate = useNavigate();
  const { authToken } = useAuth();

  const [answers, setAnswers] = useState({});
  const [quizDetails, setQuizDetails] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!id || !attemptId) {
      setError('Quiz ID or Attempt ID missing from URL.');
      setLoading(false);
      return;
    }

    const fetchAttemptData = async () => {
      if (!authToken) {
        setError('Not authenticated. Please log in.');
        setLoading(false);
        return;
      }
      try {
        const attemptRes = await api.get(`student-quizzes/${attemptId}/`);
        const data = attemptRes.data;

        setQuizDetails({
          title: data.quiz_title || data.quiz?.title || 'Quiz',
          description: data.quiz_description || data.quiz?.description || '',
          duration_minutes: data.duration_minutes ?? data.quiz?.duration_minutes ?? 60,
        });

        setQuestions(Array.isArray(data.questions) ? data.questions : []);
      } catch (err) {
        console.error('Error fetching quiz attempt:', err);
        setError('Failed to load quiz.');
        toast.error('Failed to load quiz.');
      } finally {
        setLoading(false);
      }
    };

    fetchAttemptData();
  }, [id, attemptId, authToken]);

  const handleAnswerChange = (questionId, answer) => {
    setAnswers((prev) => ({ ...prev, [questionId]: answer }));
  };

  const handleSubmit = async () => {
    if (questions.length === 0) {
      toast.error('No questions to submit.');
      return;
    }

    const submissions = questions
      .map((q) => {
        const qid = Number(q.id);
        const ans = answers[qid] ?? answers[String(qid)] ?? '';
        return {
          question: qid,
          answer_text: typeof ans === 'number' ? String(ans) : ans ?? '',
        };
      })
      .filter((s) => s.answer_text !== '' && s.answer_text !== null);

    if (submissions.length === 0) {
      toast.error('Please answer at least one question.');
      return;
    }

    try {
      const res = await api.post(`student-quizzes/${attemptId}/submit_quiz/`, { submissions });
      toast.success('Quiz submitted successfully!');
      
      // Pass result data to results page via state
      navigate(`/results/${attemptId}`, { state: { result: res.data } });
    } catch (err) {
      console.error('Error submitting quiz:', err);
      const message = err.response?.data?.error || err.message || 'Submission failed. Please try again.';
      toast.error(message);
    }
  };

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="container mx-auto mt-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-gray-800">{quizDetails?.title}</h2>
        <Timer durationMinutes={quizDetails?.duration_minutes} onTimeUp={handleSubmit} />
      </div>

      <div className="space-y-6">
        {questions.length > 0 ? (
          questions.map((question) => (
            <Question
              key={question.id}
              question={question}
              onAnswerChange={handleAnswerChange}
              currentAnswer={answers[question.id] ?? answers[String(question.id)]}
            />
          ))
        ) : (
          <p className="text-gray-500">No questions available for this quiz.</p>
        )}
      </div>

      <button
        onClick={handleSubmit}
        className="btn btn-primary w-full mt-8 bg-blue-600 hover:bg-blue-700 text-white py-3 rounded-lg transition-colors"
        aria-label="Submit Quiz"
      >
        Submit Quiz
      </button>
    </div>
  );
};

export default QuizPage;
