// src/components/LoginForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
 // ✅ Import auth context

const API_BASE_URL = "http://localhost:8000/api";

const LoginForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const { login, setIsLoggedIn } = useAuth(); // ✅ Use login + setIsLoggedIn from context

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.username.trim()) {
      newErrors.username = 'Username is required.';
    }
    if (!formData.password.trim()) {
      newErrors.password = 'Password is required.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error('Please correct the form errors.');
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/users/login/`, {
        username: formData.username,
        password: formData.password,
      });

      // ✅ Store login data via AuthContext
      login(
        response.data.token,     // token
        response.data.user_id,   // user ID (ensure API returns this key)
        response.data.email      // email (ensure API returns this key)
      );

      // ✅ Immediately update state so UI reacts before reload
      setIsLoggedIn(true);

      toast.success('Login successful! Redirecting...');

      // ✅ Redirect after login
      navigate('/quiz');

    } catch (err) {
      console.error('Login failed:', err.response?.data || err.message);
      const errorMessage =
        err.response?.data?.detail ||
        err.response?.data?.non_field_errors?.[0] ||
        'Invalid username or password.';
      toast.error(errorMessage);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md card">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label" htmlFor="username">Username</label>
            <input
              id="username"
              type="text"
              className={`form-input ${errors.username ? 'border-red-500' : ''}`}
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
            {errors.username && <p className="text-red-500 text-sm mt-1">{errors.username}</p>}
          </div>
          <div className="form-group">
            <label className="form-label" htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              className={`form-input ${errors.password ? 'border-red-500' : ''}`}
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
          </div>
          <button type="submit" className="btn btn-primary w-full mt-4">
            Log In
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
