// src/services/api.js
import axios from "axios";

// Debug: Check if environment variable is loaded
console.log("ENV API URL:", process.env.REACT_APP_API_URL);

// ✅ Use correct backend URL without trailing slash
const API_URL = (process.env.REACT_APP_API_URL || "http://localhost:8000/api").replace(/\/$/, "");

const api = axios.create({
  baseURL: API_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// ✅ Request Interceptor – Attach JWT access token from localStorage
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("access_token"); // updated key name
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// ✅ Response Interceptor – Handle expired tokens & global errors
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const status = error.response?.status;
    const originalRequest = error.config;

    // Handle expired access token
    if (status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      const refreshToken = localStorage.getItem("refresh_token"); // updated key name
      if (refreshToken) {
        try {
          const refreshResponse = await axios.post(
            `${API_URL}/token/refresh/`,
            { refresh: refreshToken },
            { headers: { "Content-Type": "application/json" } }
          );

          const newAccessToken = refreshResponse.data.access;
          if (newAccessToken) {
            localStorage.setItem("access_token", newAccessToken); // updated key name
            originalRequest.headers.Authorization = `Bearer ${newAccessToken}`;
            return api(originalRequest);
          }
        } catch (refreshError) {
          console.warn("Token refresh failed:", refreshError);

          if (
            refreshError.response?.data?.code === "token_not_valid" ||
            refreshError.response?.data?.detail === "Token is blacklisted"
          ) {
            clearAuthDataAndRedirect();
            return Promise.reject(refreshError);
          }
        }
      }

      clearAuthDataAndRedirect();
      return Promise.reject(error);
    }

    if (status === 403) {
      console.warn("Forbidden: You do not have permission to perform this action.");
    }

    return Promise.reject(error);
  }
);

// ✅ Clear localStorage and redirect to login
function clearAuthDataAndRedirect() {
  localStorage.removeItem("access_token");
  localStorage.removeItem("refresh_token");
  localStorage.removeItem("username");
  localStorage.removeItem("email");
  localStorage.removeItem("userId");
  localStorage.removeItem("quiz_isAuthenticated");
  window.location.href = "/login";
}

// ✅ Login function – sends email input as username
export const loginUser = (email, password) => {
  return api.post("/token/", {
    username: email, // backend expects 'username'
    password,
  });
};

// ✅ Refresh access token
export const refreshToken = (refresh) => {
  return api.post("/token/refresh/", { refresh });
};

// ✅ Logout function
export const logoutUser = () => {
  clearAuthDataAndRedirect();
};

export default api;
