// src/pages/ResultsPage.js
import React from 'react';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { useAuth } from '../contexts/AuthContext';
import useApi from '../hooks/useApi';

const ResultsPage = () => {
  const { authToken } = useAuth(); // get token from context
  const token = authToken || localStorage.getItem('access'); // fallback to localStorage

  // Pass the token as an Authorization header in the request
  const { data: results, loading, error } = useApi('student-quizzes/', {
    headers: {
      Authorization: `Token ${token}`,
    },
  });

  if (loading) {
    return <Loader />;
  }

  if (error) {
    return <ErrorMessage message="Failed to load quiz results." />;
  }

  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">My Quiz Results</h2>
      <div className="space-y-6">
        {results.map((result) => (
          <div key={result.id} className="card">
            <h3 className="text-2xl font-semibold mb-2">{result.quiz.title}</h3>
            <p className="text-gray-600 mb-2">
              Completed on: {new Date(result.completed_at).toLocaleDateString()}
            </p>
            <p className="text-xl font-bold text-indigo-600">
              Final Score: {result.total_score}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ResultsPage;
