import React, { useEffect, useState } from 'react';
import api from '../services/api';  // centralized axios instance with interceptor
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { useAuth } from '../contexts/AuthContext';

const Leaderboard = () => {
  const [leaderboardData, setLeaderboardData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { authToken } = useAuth();

  useEffect(() => {
    if (!authToken) {
      setLoading(false);
      setError('User is not authenticated. Please log in.');
      return;
    }

    const fetchLeaderboard = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await api.get('/student-quizzes/leaderboard/');
        // Defensive: Check if response.data is array
        if (Array.isArray(response.data)) {
          // Sorting by total_score descending, fallback if key doesn't exist
          const sortedData = [...response.data].sort((a, b) => {
            return (b.total_score || 0) - (a.total_score || 0);
          });
          setLeaderboardData(sortedData);
        } else {
          setError('Invalid data format received from server.');
        }
      } catch (err) {
        console.error('Failed to fetch leaderboard:', err);
        setError('Failed to load the leaderboard. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [authToken]);

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  if (leaderboardData.length === 0) {
    return (
      <div className="container mx-auto mt-8 text-center">
        <h2 className="text-3xl font-bold mb-4 text-gray-800">Leaderboard</h2>
        <p className="text-gray-500">No leaderboard data available yet.</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto mt-8 px-4">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">Leaderboard</h2>
      <div className="overflow-x-auto shadow-lg rounded-lg">
        <table className="min-w-full divide-y divide-gray-200 bg-white">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Rank</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">User</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Quiz</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Score</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {leaderboardData.map((entry, index) => (
              <tr key={entry.student_id || `${entry.student_username}-${index}`}>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                  {index + 1}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                  {entry.student_username || 'Unknown'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {entry.quiz_title || '—'}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-indigo-600">
                  {entry.score ?? entry.total_score ?? 0}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Leaderboard;
