import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import api from '../services/api';
import { FaArrowLeft, FaCheckCircle, FaTimesCircle } from 'react-icons/fa';
import { useAuth } from '../contexts/AuthContext';

const ResultsDetailPage = () => {
  const { attemptId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { isLoggedIn } = useAuth();

  const [resultData, setResultData] = useState(location.state?.result || null);
  const [loading, setLoading] = useState(!resultData);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!isLoggedIn) {
      setError('You must be logged in to view results.');
      setLoading(false);
      return;
    }

    if (!resultData) {
      const fetchResult = async () => {
        try {
          const res = await api.get(`/student-quizzes/${attemptId}/`);
          setResultData(res.data);
        } catch (err) {
          setError('Failed to load result details.');
        } finally {
          setLoading(false);
        }
      };
      fetchResult();
    }
  }, [attemptId, resultData, isLoggedIn]);

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;
  if (!resultData)
    return (
      <p className="text-center text-gray-500 mt-6">No result data available.</p>
    );

  const {
    quiz_title = 'Quiz Result',
    total_score = 0,
    total_possible_points = 0,
    attempted = 0,
    unattempted = 0,
    time_taken_str,
    time_taken = 0,
    results = [],
  } = resultData;

  const formattedTimeTaken =
    time_taken_str ||
    (typeof time_taken === 'number'
      ? `${Math.floor(time_taken / 60)}m ${time_taken % 60}s`
      : '0m 0s');

  const scorePercent =
    total_possible_points > 0
      ? Math.round((total_score / total_possible_points) * 100)
      : 0;

  const getFeedback = (percent) => {
    if (percent >= 80) return 'Excellent work! ðŸŽ¯';
    if (percent >= 50) return 'Good job! Keep practicing. ðŸ’ª';
    return "Don't give up! Try again. ðŸš€";
  };

  return (
    <div className="container mx-auto mt-8 max-w-4xl px-4">
      <button
        onClick={() => navigate('/dashboard')}
        className="mb-4 flex items-center gap-2 text-indigo-600 hover:text-indigo-800"
      >
        <FaArrowLeft /> Back to Dashboard
      </button>

      <h2 className="text-3xl font-bold mb-6 text-center">{quiz_title}</h2>

      <div className="mb-6 grid grid-cols-2 md:grid-cols-4 gap-4 text-center bg-gray-50 p-4 rounded-lg shadow-sm">
        <div>
          <p className="font-semibold">Score</p>
          <p>
            {total_score} / {total_possible_points} ({scorePercent}%)
          </p>
        </div>
        <div>
          <p className="font-semibold">Attempted</p>
          <p>{attempted}</p>
        </div>
        <div>
          <p className="font-semibold">Unattempted</p>
          <p>{unattempted}</p>
        </div>
        <div>
          <p className="font-semibold">Time Taken</p>
          <p>{formattedTimeTaken}</p>
        </div>
      </div>

      <div className="mb-6 text-center">
        <p
          className={`text-lg font-bold ${
            scorePercent >= 80
              ? 'text-green-600'
              : scorePercent >= 50
              ? 'text-yellow-600'
              : 'text-red-600'
          }`}
        >
          {getFeedback(scorePercent)}
        </p>
      </div>

      <div className="space-y-4 mt-6">
        {results && results.length > 0 ? (
          results.map((r, idx) => (
            <div
              key={r.question_id ?? idx}
              className="p-4 border rounded-lg shadow-sm flex flex-col md:flex-row items-start md:items-center justify-between bg-white"
            >
              <div className="mb-2 md:mb-0">
                <p className="font-semibold text-gray-800">
                  {r.question_text ?? 'Question text not available'}
                </p>
                <p className="text-gray-600">
                  Score Awarded: {r.score_awarded ?? 0} / {r.points ?? 0}
                </p>
              </div>
              <div>
                {r.is_correct ? (
                  <FaCheckCircle
                    className="text-green-500 text-2xl"
                    title="Correct"
                  />
                ) : (
                  <FaTimesCircle
                    className="text-red-500 text-2xl"
                    title="Incorrect"
                  />
                )}
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">
            No question results found for this attempt.
          </p>
        )}
      </div>
    </div>
  );
};

export default ResultsDetailPage;
