import React from 'react';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import useApi from '../hooks/useApi';

const ResultsPage = () => {
  // Fetch results from API
  const { data: results, loading, error } = useApi('student-quizzes/');

  if (loading) return <Loader />;

  if (error) return <ErrorMessage message="Failed to load quiz results." />;

  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">My Quiz Results</h2>
      <div className="space-y-6">
        {Array.isArray(results) && results.length > 0 ? (
          results.map((result) => (
            <div key={result.id} className="card p-4">
              <h3 className="text-2xl font-semibold mb-2">
                {result.quiz?.title ?? result.quiz_title ?? 'Untitled Quiz'}
              </h3>
              <p className="text-gray-600 mb-2">
                Completed on:{' '}
                {result.completed_at
                  ? new Date(result.completed_at).toLocaleString()
                  : '—'}
              </p>
              <p className="text-xl font-bold text-indigo-600">
                Final Score: {result.total_score ?? 0}
              </p>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No results yet.</p>
        )}
      </div>
    </div>
  );
};

export default ResultsPage;
