import React from 'react';

const Question = ({ question, onAnswerChange, currentAnswer, disabled }) => {
  const { id, text, question_type, options } = question;

  const handleChange = (e) => {
    const { value, type, checked, name } = e.target;
    if (question_type === 'mcq') {
      // Single choice - Radio buttons
      onAnswerChange(id, Number(value));
    } else if (question_type === 'tf') {
      // True/False Toggle (checkbox here)
      onAnswerChange(id, checked);
    } else if (question_type === 'text') {
      onAnswerChange(id, value);
    }
    // For coding, this component does not handle the editor; it's separate.
  };

  if (!question) return null;

  return (
    <div className="mb-6">
      <h3 className="text-xl font-semibold mb-3">{text}</h3>

      {question_type === 'mcq' && options && (
        <div>
          {options.map((option, idx) => (
            <label key={idx} className="block mb-2">
              <input
                type="radio"
                name={`question_${id}`}
                value={idx}
                checked={currentAnswer === idx}
                onChange={handleChange}
                disabled={disabled}
                className="mr-2"
              />
              {option.text}
            </label>
          ))}
        </div>
      )}

      {question_type === 'tf' && options && (
        <label className="block mb-2">
          <input
            type="checkbox"
            checked={currentAnswer || false}
            onChange={handleChange}
            disabled={disabled}
            className="mr-2"
          />
          {text}
        </label>
      )}

      {question_type === 'text' && (
        <textarea
          rows={4}
          className="w-full p-2 border rounded"
          value={currentAnswer || ''}
          onChange={handleChange}
          disabled={disabled}
          placeholder="Type your answer here..."
        />
      )}
    </div>
  );
};

export default Question;
