// src/components/common/CodeEditor.jsx
import React from 'react';
import { Editor } from '@monaco-editor/react';

/**
 * CodeEditor component using Monaco Editor to provide
 * a rich code editing interface with syntax highlighting.
 * 
 * Props:
 * - code: current code string to display
 * - onCodeChange: callback to handle code changes
 * - language: programming language (default: 'python')
 */
const CodeEditor = ({ code, onCodeChange, language = 'python' }) => {
  // Handle editor content changes
  const handleChange = (value) => {
    onCodeChange(value || '');
  };

  return (
    <div className="border border-gray-300 rounded-md overflow-hidden">
      <Editor
        height="300px"
        defaultLanguage={language}
        value={code}
        onChange={handleChange}
        theme="vs-dark"
        options={{
          minimap: { enabled: false },
          fontSize: 14,
          scrollBeyondLastLine: false,
          automaticLayout: true,
        }}
      />
    </div>
  );
};

export default CodeEditor;
