// src/services/api.js
import axios from "axios";

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:8000/api/",
  headers: {
    "Content-Type": "application/json",
  },
});

// Request Interceptor – Attach token from localStorage
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token"); // Using 'token' as the consistent key
    if (token) {
      // TokenAuthentication (Django REST Framework default)
      config.headers.Authorization = `Token ${token}`;

      // For JWT Authentication instead:
      // config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response Interceptor – Handle unauthorized responses globally
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      console.warn("Authentication failed. Redirecting to login...");
      // Clear token & redirect if needed
      // localStorage.removeItem("token");
      // window.location.href = "/login";
    }
    return Promise.reject(error);
  }
);

export default api;
