import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import api from '../services/api';
import { FaArrowLeft, FaCheckCircle, FaTimesCircle } from 'react-icons/fa';

const ResultsDetailPage = () => {
  const { attemptId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [resultData, setResultData] = useState(location.state?.result || null);
  const [loading, setLoading] = useState(!resultData);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!resultData) {
      const fetchResult = async () => {
        try {
          const res = await api.get(`student-quizzes/${attemptId}/`);
          setResultData(res.data);
        } catch (err) {
          setError('Failed to load result details.');
        } finally {
          setLoading(false);
        }
      };
      fetchResult();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [attemptId]);  // Removed resultData from dependency array

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  if (!resultData) {
    return <p>No result data available.</p>;
  }

  const {
    quiz_title,
    total_score,
    total_questions,
    attempted_questions,
    unattempted_questions,
    time_taken_str,
    results,
  } = resultData;

  return (
    <div className="container mx-auto mt-8 max-w-4xl">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 flex items-center gap-2 text-indigo-600 hover:text-indigo-800"
      >
        <FaArrowLeft /> Back
      </button>
      <h2 className="text-3xl font-bold mb-6">{quiz_title || 'Quiz Result'}</h2>

      <div className="mb-6">
        <p>
          <strong>Total Score:</strong> {total_score} / {total_questions}
        </p>
        <p>
          <strong>Attempted Questions:</strong> {attempted_questions}
        </p>
        <p>
          <strong>Unattempted Questions:</strong> {unattempted_questions}
        </p>
        <p>
          <strong>Time Taken:</strong> {time_taken_str}
        </p>
      </div>

      <div className="space-y-4">
        {results && results.length > 0 ? (
          results.map((r) => (
            <div
              key={r.question_id}
              className="p-4 border rounded shadow-sm flex items-center justify-between"
            >
              <div>
                <p className="font-semibold">{r.question_text}</p>
                <p>
                  <strong>Score Awarded:</strong> {r.score_awarded} / {r.points}
                </p>
              </div>
              <div>
                {r.is_correct ? (
                  <FaCheckCircle className="text-green-500 text-2xl" title="Correct" />
                ) : (
                  <FaTimesCircle className="text-red-500 text-2xl" title="Incorrect" />
                )}
              </div>
            </div>
          ))
        ) : (
          <p>No question results available.</p>
        )}
      </div>
    </div>
  );
};

export default ResultsDetailPage;
