import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import Question from '../components/quiz/Question';
import Timer from '../components/quiz/Timer';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { toast } from 'react-toastify';
import axios from 'axios';

const QuizPage = () => {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const attemptId = searchParams.get('attempt');
  const navigate = useNavigate();

  const [answers, setAnswers] = useState({});
  const [quizDetails, setQuizDetails] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  console.log("QuizPage loaded with id:", id, "attemptId:", attemptId);

  useEffect(() => {
    if (!id || !attemptId) {
      setError("Quiz ID or Attempt ID missing from URL.");
      setLoading(false);
      return;
    }

    const fetchAttemptData = async () => {
      try {
        const token = localStorage.getItem('access');
        if (!token) {
          setError('No access token found.');
          toast.error('Please log in first.');
          return;
        }

        const headers = { Authorization: `Token ${token}` };

        // Fetch attempt details
        const attemptRes = await axios.get(
          `http://localhost:8000/api/attempts/${attemptId}/`,
          { headers }
        );

        setQuizDetails(attemptRes.data.quiz);
        setQuestions(attemptRes.data.questions);

      } catch (err) {
        console.error('Failed to fetch attempt data:', err);
        setError('Failed to load quiz.');
        toast.error('Failed to load quiz.');
      } finally {
        setLoading(false);
      }
    };

    fetchAttemptData();
  }, [id, attemptId]);

  const handleAnswerChange = (questionId, answer) => {
    setAnswers((prevAnswers) => ({
      ...prevAnswers,
      [questionId]: answer,
    }));
  };

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem('access');
      const headers = { Authorization: `Token ${token}` };

      const submissions = Object.keys(answers).map((questionId) => ({
        question_id: questionId,
        answer: answers[questionId],
      }));

      await axios.post(
        `http://localhost:8000/api/attempts/${attemptId}/submit/`,
        { submissions },
        { headers }
      );

      toast.success('Quiz submitted successfully!');
      navigate(`/results/${attemptId}`);

    } catch (err) {
      console.error('Submission failed:', err);
      toast.error('Submission failed. Please try again.');
    }
  };

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="container mx-auto mt-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-gray-800">
          {quizDetails?.title || 'Quiz'}
        </h2>
        <Timer
          durationMinutes={quizDetails?.duration_minutes || 60}
          onTimeUp={handleSubmit}
        />
      </div>

      <div className="space-y-6">
        {questions.map((question) => (
          <Question
            key={question.id}
            question={question}
            onAnswerChange={handleAnswerChange}
            currentAnswer={answers[question.id]}
          />
        ))}
      </div>

      <button onClick={handleSubmit} className="btn btn-primary w-full mt-8">
        Submit Quiz
      </button>
    </div>
  );
};

export default QuizPage;
