// Updated with auto redirect if token missing
import React, { useEffect, useState } from 'react';
import api from '../services/api';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { useAuth } from '../contexts/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import { FaClock, FaPlayCircle } from 'react-icons/fa';

const Dashboard = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statusMessage, setStatusMessage] = useState(null);

  const { accessToken, isLoggedIn } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isLoggedIn || !accessToken) {
      navigate('/login', { replace: true });
      return;
    }

    const fetchQuizzes = async () => {
      try {
        const response = await api.get('/quizzes/');
        setQuizzes(response.data || []);
      } catch (err) {
        setError(
          err.response?.data?.error ||
            'Failed to load quizzes. Please try again later.'
        );
      } finally {
        setLoading(false);
      }
    };

    fetchQuizzes();
  }, [isLoggedIn, accessToken, navigate]);

  const handleStartQuiz = async (quizId) => {
    if (!isLoggedIn || !accessToken) {
      setStatusMessage({
        type: 'error',
        text: 'You need to log in before starting a quiz.',
      });
      navigate('/login', { replace: true });
      return;
    }

    try {
      const response = await api.post(`/quizzes/${quizId}/start/`);
      if (response.status === 200 && response.data?.attempt_id) {
        setStatusMessage({
          type: 'success',
          text: 'Quiz started successfully! Redirecting...',
        });

        setTimeout(() => {
          navigate(`/quiz/${quizId}?attempt=${response.data.attempt_id}`);
        }, 500);
      } else {
        setStatusMessage({
          type: 'error',
          text:
            response.data?.error || 'Failed to start quiz. Please try again.',
        });
      }
    } catch (err) {
      setStatusMessage({
        type: 'error',
        text:
          err.response?.data?.error ||
          'Failed to start quiz. Please try again.',
      });
    }
  };

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="container mx-auto mt-8 px-4">
      <h2 className="text-3xl font-bold mb-6 text-indigo-800 text-center">
        Available Quizzes
      </h2>

      {statusMessage && (
        <div
          className={`mb-4 p-3 rounded-lg shadow-md text-center ${
            statusMessage.type === 'success'
              ? 'bg-green-100 text-green-700 border border-green-300'
              : 'bg-red-100 text-red-700 border border-red-300'
          }`}
          role="alert"
        >
          {statusMessage.text}
        </div>
      )}

      {quizzes.length === 0 ? (
        <p className="text-gray-600 text-center">
          No quizzes available at the moment.
        </p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {quizzes.map((quiz) => (
            <div
              key={quiz.id}
              className="bg-white rounded-xl shadow-lg border border-gray-200 hover:shadow-2xl transition duration-300 p-6 flex flex-col justify-between"
            >
              <div>
                <h3 className="text-xl font-semibold mb-2 text-gray-800">
                  <Link
                    to={`/quiz/${quiz.id}`}
                    className="hover:text-indigo-600 transition"
                    title={quiz.title ?? 'Untitled Quiz'}
                  >
                    {quiz.title ?? 'Untitled Quiz'}
                  </Link>
                </h3>
                <p className="text-gray-600 mb-4">
                  {quiz.description ?? 'No description provided.'}
                </p>
                <div className="flex items-center gap-2 text-sm text-gray-500 mb-4">
                  <FaClock className="text-indigo-500" />
                  <span>
                    Duration: {quiz.duration_minutes ?? 0} minutes
                  </span>
                </div>
              </div>

              <button
                onClick={() => handleStartQuiz(quiz.id)}
                className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-full shadow-md transition duration-300"
                aria-label={`Start quiz: ${quiz.title ?? 'Quiz'}`}
              >
                <FaPlayCircle /> Start Quiz
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
