// src/components/auth/LoginForm.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api'; // Centralized axios instance
import { toast } from 'react-toastify';
import { useAuth } from '../../contexts/AuthContext';

const LoginForm = () => {
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors((prev) => ({ ...prev, [e.target.name]: '' }));
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.username.trim()) newErrors.username = 'Username is required.';
    if (!formData.password.trim()) newErrors.password = 'Password is required.';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error('Please correct the form errors.');
      return;
    }

    try {
      // 1️⃣ Request tokens from SimpleJWT endpoint
      const tokenResponse = await api.post('/token/', {
        username: formData.username,
        password: formData.password,
      });

      const { access, refresh } = tokenResponse.data;

      if (!access) {
        toast.error('Login failed: No access token returned.');
        return;
      }

      // Save tokens in localStorage
      localStorage.setItem('access_token', access);
      if (refresh) localStorage.setItem('refresh_token', refresh);
      localStorage.setItem('quiz_isAuthenticated', 'true');

      // 2️⃣ Fetch user info after login
      let userId = null;
      let userEmail = '';
      let userName = '';

      try {
        const userResponse = await api.get('/users/me/', {
          headers: { Authorization: `Bearer ${access}` },
        });
        userId = userResponse.data.id;
        userEmail = userResponse.data.email;
        userName = userResponse.data.username;

        localStorage.setItem('user_id', userId);
        localStorage.setItem('user_email', userEmail);
        localStorage.setItem('user_name', userName || '');
      } catch (userError) {
        console.error('Failed to fetch user info:', userError);
      }

      // Update AuthContext
      login(access, userId, userEmail, userName);

      toast.success('Login successful! Redirecting...');
      navigate('/dashboard');
    } catch (err) {
      console.error('Login failed:', err.response?.data || err.message);

      // Clear auth data on login failure
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');
      localStorage.removeItem('user_id');
      localStorage.removeItem('user_email');
      localStorage.removeItem('user_name');
      localStorage.removeItem('quiz_isAuthenticated');

      const errorMessage =
        err.response?.data?.detail ||
        err.response?.data?.non_field_errors?.[0] ||
        'Invalid username or password.';
      toast.error(errorMessage);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md p-6 shadow-md bg-white rounded">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">Login</h2>
        <form onSubmit={handleSubmit} noValidate>
          <div className="mb-4">
            <label htmlFor="username" className="block mb-1 font-semibold">
              Username
            </label>
            <input
              id="username"
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              autoComplete="username"
              className={`w-full border px-3 py-2 rounded ${
                errors.username ? 'border-red-500' : 'border-gray-300'
              }`}
            />
            {errors.username && <p className="text-red-500 text-sm mt-1">{errors.username}</p>}
          </div>

          <div className="mb-4">
            <label htmlFor="password" className="block mb-1 font-semibold">
              Password
            </label>
            <input
              id="password"
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              autoComplete="current-password"
              className={`w-full border px-3 py-2 rounded ${
                errors.password ? 'border-red-500' : 'border-gray-300'
              }`}
            />
            {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
          </div>

          <button
            type="submit"
            className="w-full py-2 rounded bg-blue-600 hover:bg-blue-700 text-white font-semibold transition-colors duration-200"
          >
            Log In
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
