// src/services/api.js
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8000/api/";

const api = axios.create({
  baseURL: API_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// Request Interceptor – Attach JWT access token from localStorage
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("access_token"); // ✅ match backend JWT storage key

    if (token) {
      // JWT format expected by DRF SimpleJWT
      config.headers.Authorization = `Bearer ${token}`;
    }

    return config;
  },
  (error) => Promise.reject(error)
);

// Response Interceptor – Global error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    const status = error.response?.status;

    if (status === 401) {
      console.warn("Unauthorized: Redirecting to login...");
      // Clear all auth-related storage
      localStorage.removeItem("access_token");
      localStorage.removeItem("refresh_token");
      localStorage.removeItem("quiz_user");
      localStorage.removeItem("quiz_isAuthenticated");

      // Redirect to login
      window.location.href = "/login";
    } else if (status === 403) {
      console.warn("Forbidden: You do not have permission to perform this action.");
      // Optional: trigger a UI toast/notification here
    }

    return Promise.reject(error);
  }
);

export default api;
