// src/services/api.js
import axios from 'axios';

// Use the backend service name from docker-compose instead of localhost in Docker
// In dev outside Docker, fallback to localhost
const API_BASE_URL =
  process.env.REACT_APP_API_URL || 'http://localhost:8000/api/';

// Create an instance of axios with a base URL
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true, // ✅ send cookies for CSRF/session auth
});

// Request interceptor to add the authentication token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Token ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      console.warn('Authentication token expired or invalid. Please log in again.');
      // Optional: clear local storage and redirect
      // localStorage.removeItem('authToken');
      // window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
