// src/services/api.js
import axios from "axios";

// âœ… Use correct backend URL with trailing /api
const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8000/api";

const api = axios.create({
  baseURL: API_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// âœ… Request Interceptor â€“ Attach JWT access token from localStorage
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("authToken"); // Note updated key to 'authToken' for consistency
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// âœ… Response Interceptor â€“ Handle expired tokens & global errors
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const status = error.response?.status;
    const originalRequest = error.config;

    // ðŸŸ¢ Handle expired/invalid token (401) with refresh attempt
    if (status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      const refreshToken = localStorage.getItem("refreshToken"); // Also updated key to 'refreshToken'
      if (refreshToken) {
        try {
          const refreshResponse = await axios.post(
            `${API_URL}/token/refresh/`,
            { refresh: refreshToken },
            { headers: { "Content-Type": "application/json" } }
          );

          const newAccessToken = refreshResponse.data.access;
          if (newAccessToken) {
            localStorage.setItem("authToken", newAccessToken);
            originalRequest.headers.Authorization = `Bearer ${newAccessToken}`;
            return api(originalRequest); // Retry the original request
          }
        } catch (refreshError) {
          console.warn("Token refresh failed:", refreshError);

          // If token is blacklisted or not valid, stop retrying
          if (
            refreshError.response?.data?.code === "token_not_valid" ||
            refreshError.response?.data?.detail === "Token is blacklisted"
          ) {
            clearAuthDataAndRedirect();
            return Promise.reject(refreshError);
          }
        }
      }

      // âŒ If refresh fails or no refresh token, clear data & redirect
      clearAuthDataAndRedirect();
      return Promise.reject(error);
    }

    if (status === 403) {
      console.warn("Forbidden: You do not have permission to perform this action.");
    }

    return Promise.reject(error);
  }
);

// Helper to clear all auth-related storage and redirect to login
function clearAuthDataAndRedirect() {
  localStorage.removeItem("authToken");
  localStorage.removeItem("refreshToken");
  localStorage.removeItem("userId");
  localStorage.removeItem("userEmail");
  localStorage.removeItem("userName");
  localStorage.removeItem("quiz_isAuthenticated");
  window.location.href = "/login";
}

// ðŸ†• Login helper â€“ send to /users/login/ with email and password keys
export const loginUser = (email, password) => {
  return api.post("/users/login/", { email, password });
};

export default api;
