// src/components/common/ErrorMessage.jsx
import React from 'react';

const ErrorMessage = ({ message }) => {
  return (
    <div
      role="alert"
      className="flex justify-center items-center min-h-[150px] my-12 px-4"
    >
      <div className="text-red-600 text-lg font-semibold bg-red-100 p-4 rounded shadow">
        Error: {message || 'Something went wrong.'}
      </div>
    </div>
  );
};

export default ErrorMessage;
