import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
//import api from '../services/api';
import Question from '../components/quiz/Question';
import Timer from '../components/quiz/Timer';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import useApi from '../hooks/useApi';
import { useAuth } from '../contexts/AuthContext';
import { toast } from 'react-toastify';
import axios from 'axios'; // Added to make direct requests with proper headers

const QuizPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { authToken } = useAuth();

  console.log("QuizPage loaded with id:", id);

  // Ensure API hook also includes token header if needed
  const { data: questions, loading: questionsLoading, error: questionsError } =
    useApi(id ? `quizzes/${id}/` : null);

  const [answers, setAnswers] = useState({});
  const [quizDetails, setQuizDetails] = useState(null);
  const [quizLoading, setQuizLoading] = useState(true);
  const [quizError, setQuizError] = useState(null);

  useEffect(() => {
    if (!id) {
      console.error("No quiz ID found in URL.");
      return;
    }
    const fetchQuizDetails = async () => {
      try {
        const token = localStorage.getItem('access');
        const response = await axios.get(`http://localhost:8000/api/quizzes/${id}/`, {
          headers: {
            Authorization: `Token ${token}`, // ✅ Correct token format
          },
        });
        setQuizDetails(response.data);
      } catch (err) {
        console.error('Failed to fetch quiz details:', err);
        setQuizError(err);
        toast.error('Failed to load quiz details.');
      } finally {
        setQuizLoading(false);
      }
    };
    fetchQuizDetails();
  }, [id]);

  const handleAnswerChange = (questionId, answer) => {
    setAnswers((prevAnswers) => ({
      ...prevAnswers,
      [questionId]: answer,
    }));
  };

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem('access');
      const submissions = Object.keys(answers).map((questionId) => ({
        question_id: questionId,
        answer: answers[questionId],
      }));

      await axios.post(
        `http://localhost:8000/api/quizzes/${id}/submit/`,
        { submissions },
        {
          headers: {
            Authorization: `Token ${token}`, // ✅ Correct token format
          },
        }
      );

      toast.success('Quiz submitted successfully!');
      navigate('/results');
    } catch (err) {
      console.error('Submission failed:', err);
      toast.error('Submission failed. Please try again.');
    }
  };

  if (!id) {
    return <ErrorMessage message="No quiz ID provided in the URL." />;
  }

  if (questionsLoading || quizLoading) {
    return <Loader />;
  }

  if (questionsError || quizError) {
    return <ErrorMessage message="Failed to load quiz or submit answers." />;
  }

  return (
    <div className="container mx-auto mt-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-gray-800">
          {quizDetails?.title || 'Quiz'}
        </h2>
        <Timer
          durationMinutes={quizDetails?.duration_minutes || 60}
          onTimeUp={handleSubmit}
        />
      </div>
      <div className="space-y-6">
        {questions?.map((question) => (
          <Question
            key={question.id}
            question={question}
            onAnswerChange={handleAnswerChange}
            currentAnswer={answers[question.id]}
          />
        ))}
      </div>
      <button onClick={handleSubmit} className="btn btn-primary w-full mt-8">
        Submit Quiz
      </button>
    </div>
  );
};

export default QuizPage;
