import React from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isLoggedIn, onLogout }) => {
  return (
    <header className="bg-white shadow-sm sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <Link to="/">
          <span className="text-xl font-bold text-gray-800">B.Tech Exams</span>
        </Link>
        <nav className="hidden md:flex space-x-8 items-center">
          <Link to="/" className="text-gray-600 hover:text-indigo-600 transition-colors duration-200">
            Dashboard
          </Link>
          <Link to="/results" className="text-gray-600 hover:text-indigo-600 transition-colors duration-200">
            My Results
          </Link>
          <Link to="/leaderboard" className="text-gray-600 hover:text-indigo-600 transition-colors duration-200">
            Leaderboard
          </Link>
          {isLoggedIn ? (
            <button onClick={onLogout} className="btn btn-primary">
              Logout
            </button>
          ) : (
            <>
              <Link to="/login" className="btn btn-primary">
                Login
              </Link>
              <Link to="/signup" className="btn btn-secondary">
                Sign Up
              </Link>
            </>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
