import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';

import Header from './components/shared/Header';
import Footer from './components/shared/Footer';
import LoginForm from './components/user/LoginForm';
import SignupForm from './components/user/SignupForm';
import Dashboard from './pages/Dashboard';
import QuizPage from './pages/QuizPage';
import ResultsPage from './pages/ResultsPage';
import ResultsDetailPage from './pages/ResultsDetailPage';
import Leaderboard from './pages/Leaderboard';

const PrivateRoute = ({ children }) => {
  const authToken = localStorage.getItem('authToken');
  return authToken ? children : <Navigate to="/login" replace />;
};

const App = () => {
  // Optionally, you can keep isLoggedIn from context for header
  // but route protection uses localStorage check for authToken
  // So you can remove useAuth import or keep if Header needs it

  return (
    <div className="flex flex-col min-h-screen">
      <Header /> {/* Pass props if needed */}

      <main className="flex-grow">
        <Routes>
          {/* Public routes */}
          <Route path="/login" element={<LoginForm />} />
          <Route path="/signup" element={<SignupForm />} />

          {/* Protected routes */}
          <Route
            path="/dashboard"
            element={
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            }
          />
          <Route
            path="/quiz/:id"
            element={
              <PrivateRoute>
                <QuizPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/results"
            element={
              <PrivateRoute>
                <ResultsPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/results/:attemptId"
            element={
              <PrivateRoute>
                <ResultsDetailPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/leaderboard"
            element={
              <PrivateRoute>
                <Leaderboard />
              </PrivateRoute>
            }
          />

          {/* Redirect root "/" to /dashboard if logged in, else to /login */}
          <Route
            path="/"
            element={
              localStorage.getItem('authToken') ? (
                <Navigate to="/dashboard" replace />
              ) : (
                <Navigate to="/login" replace />
              )
            }
          />

          {/* Catch-all */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </main>

      <Footer />
    </div>
  );
};

export default App;
