// src/components/auth/LoginForm.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';
import { toast } from 'react-toastify';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.username.trim()) {
      newErrors.username = 'Username is required.';
    }
    if (!formData.password) {
      newErrors.password = 'Password is required.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast.error('Please correct the form errors.');
      return;
    }

    try {
      const res = await api.post('/users/login/', {
        username: formData.username,
        password: formData.password,
      });

      // Save JWT tokens
      localStorage.setItem('access', res.data.access);
      localStorage.setItem('refresh', res.data.refresh);

      toast.success('Login successful!');
      navigate('/dashboard'); // redirect to dashboard or home
    } catch (err) {
      console.error('Login failed:', err.response?.data || err.message);
      const serverErrors = err.response?.data || {};
      let errorMessage = 'Login failed. Please check your credentials.';
      if (serverErrors.detail) errorMessage = serverErrors.detail;
      toast.error(errorMessage);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md card p-6 shadow-md bg-white rounded">
        <h2 className="text-3xl font-bold text-center mb-6 text-gray-800">
          Login
        </h2>
        <form onSubmit={handleSubmit} noValidate>
          <div className="form-group mb-4">
            <label className="form-label block mb-1 font-semibold" htmlFor="username">
              Username
            </label>
            <input
              id="username"
              type="text"
              className={`form-input w-full border px-3 py-2 rounded ${errors.username ? 'border-red-500' : 'border-gray-300'}`}
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              autoComplete="username"
            />
            {errors.username && <p className="text-red-500 text-sm mt-1">{errors.username}</p>}
          </div>
          <div className="form-group mb-4">
            <label className="form-label block mb-1 font-semibold" htmlFor="password">
              Password
            </label>
            <input
              id="password"
              type="password"
              className={`form-input w-full border px-3 py-2 rounded ${errors.password ? 'border-red-500' : 'border-gray-300'}`}
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              autoComplete="current-password"
            />
            {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
          </div>
          <button
            type="submit"
            className="btn btn-primary w-full py-2 rounded bg-blue-600 hover:bg-blue-700 text-white font-semibold"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
