// src/services/api.js
import axios from "axios";

// Create an Axios instance with your backend's base URL
const api = axios.create({
  baseURL: "http://localhost:8000/api/", // change if using proxy
  headers: {
    "Content-Type": "application/json",
  },
});

// Interceptor to attach token from localStorage to every request
api.interceptors.request.use(
  (config) => {
    // We stored token under "token" in LoginForm.js after login
    const token = localStorage.getItem("token");
    if (token) {
      config.headers.Authorization = `Token ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Optional: Handle global 401 Unauthorized responses
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      console.warn("Authentication failed. Redirecting to login...");
      // Optional: clear token and redirect
      // localStorage.removeItem("token");
      // window.location.href = "/login";
    }
    return Promise.reject(error);
  }
);

export default api;
