// src/services/api.js
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8000/api";


const api = axios.create({
  baseURL: API_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// ‚úÖ Request Interceptor ‚Äì Attach JWT access token from localStorage
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("access_token");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// ‚úÖ Response Interceptor ‚Äì Handle expired tokens & global errors
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const status = error.response?.status;
    const originalRequest = error.config;

    // üü¢ Handle expired token (401) with refresh attempt
    if (status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      const refreshToken = localStorage.getItem("refresh_token");
      if (refreshToken) {
        try {
          const refreshResponse = await axios.post(
            `${API_URL}token/refresh/`,
            { refresh: refreshToken },
            { headers: { "Content-Type": "application/json" } }
          );

          const newAccessToken = refreshResponse.data.access;
          if (newAccessToken) {
            localStorage.setItem("access_token", newAccessToken);
            originalRequest.headers.Authorization = `Bearer ${newAccessToken}`;
            return api(originalRequest); // Retry the original request
          }
        } catch (refreshError) {
          console.warn("Token refresh failed:", refreshError);
        }
      }

      // ‚ùå If refresh fails, clear data & redirect
      clearAuthDataAndRedirect();
    }

    if (status === 403) {
      console.warn("Forbidden: You do not have permission to perform this action.");
      // Optionally, show a toast or notification here
    }

    return Promise.reject(error);
  }
);

// Helper to clear all auth-related storage and redirect to login
function clearAuthDataAndRedirect() {
  localStorage.removeItem("access_token");
  localStorage.removeItem("refresh_token");
  localStorage.removeItem("user_id");
  localStorage.removeItem("user_email");
  localStorage.removeItem("user_name");
  localStorage.removeItem("quiz_isAuthenticated");
  window.location.href = "/login";
}

export default api;
