// src/components/auth/LoginForm.jsx
import React, { useState } from 'react';
import api from '../../services/api';
import { useAuth } from '../../contexts/AuthContext';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

const LoginForm = () => {
  const { login } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Step 1: Request tokens from backend
      const response = await api.post('/token/', {
        username: username.trim(),
        password: password.trim(),
      });

      const {
        access,
        refresh,
        username: resUsername,
        email
      } = response.data;

      // Step 2: Decode token to get userId
      let userId = null;
      try {
        const decoded = jwtDecode(access);
        userId = decoded?.user_id || null;
      } catch (err) {
        console.error("Error decoding access token:", err);
      }

      // Step 3: Save tokens and user info in localStorage (same keys everywhere)
      localStorage.setItem('access_token', access);
      localStorage.setItem('refresh_token', refresh);
      if (userId) localStorage.setItem('userId', userId);
      localStorage.setItem('userEmail', email || '');
      localStorage.setItem('userName', resUsername || username);

      // Step 4: Update AuthContext
      login(
        access,                      // access token
        userId,                      // user ID
        resUsername || username,     // username
        email || '',                  // email
        refresh                       // refresh token
      );

      // Step 5: Notify and redirect
      toast.success('Login successful!');
      navigate('/dashboard', { replace: true });
    } catch (error) {
      console.error('Login failed:', error.response?.data || error.message);
      toast.error(error.response?.data?.detail || 'Invalid credentials!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="login-form">
      <div>
        <label>Username</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          disabled={loading}
        />
      </div>

      <div>
        <label>Password</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
        />
      </div>

      <button type="submit" disabled={loading}>
        {loading ? 'Logging in...' : 'Login'}
      </button>
    </form>
  );
};

export default LoginForm;
