// src/contexts/AuthContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [authToken, setAuthToken] = useState(null);
  const [userId, setUserId] = useState(null);
  const [userEmail, setUserEmail] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // ✅ Added separate login state

  const login = (token, id, email) => {
    localStorage.setItem('authToken', token);
    localStorage.setItem('userId', id);
    localStorage.setItem('userEmail', email);
    setAuthToken(token);
    setUserId(id);
    setUserEmail(email);
    setIsLoggedIn(true); // ✅ Instantly mark user as logged in
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userId');
    localStorage.removeItem('userEmail');
    setAuthToken(null);
    setUserId(null);
    setUserEmail(null);
    setIsLoggedIn(false); // ✅ Update immediately
  };

  // Restore from localStorage on page reload
  useEffect(() => {
    const storedToken = localStorage.getItem('authToken');
    const storedUserId = localStorage.getItem('userId');
    const storedUserEmail = localStorage.getItem('userEmail');

    if (storedToken && storedUserId && storedUserEmail) {
      setAuthToken(storedToken);
      setUserId(storedUserId);
      setUserEmail(storedUserEmail);
      setIsLoggedIn(true); // ✅ Restore login state
    }
  }, []);

  const value = {
    authToken,
    userId,
    userEmail,
    isLoggedIn,
    setIsLoggedIn, // ✅ Expose for manual control in LoginForm
    login,
    logout,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
