import React, { useState } from 'react';
import CodeEditor from './CodeEditor';
import api from '../services/api';

const Question = ({ question, onAnswerChange, currentAnswer }) => {
  const [runOutput, setRunOutput] = useState('');
  const [loading, setLoading] = useState(false);

  const runCode = async (mode) => {
    if (!currentAnswer?.trim()) {
      setRunOutput('Please provide code before running.');
      return;
    }
    try {
      setLoading(true);
      setRunOutput(mode === 'run' ? 'Running code...' : 'Testing code...');
      const res = await api.post(`/questions/${question.id}/${mode}/`, {
        code: currentAnswer,
      });
      setRunOutput(res.data.output || 'No output returned.');
    } catch (err) {
      setRunOutput(`Error: ${err.response?.data?.error || err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const renderMCQ = () => (
    <div className="space-y-4">
      {question.choices?.map((choice) => (
        <label key={choice.id} className="block cursor-pointer">
          <input
            type="radio"
            name={`question-${question.id}`}
            value={choice.choice_text}
            checked={currentAnswer === choice.choice_text}
            onChange={(e) => onAnswerChange(question.id, e.target.value)}
            className="mr-2"
          />
          {choice.choice_text}
        </label>
      ))}
    </div>
  );

  const renderText = () => (
    <input
      type="text"
      className="form-input w-full"
      value={currentAnswer || ''}
      onChange={(e) => onAnswerChange(question.id, e.target.value)}
      placeholder="Type your answer here..."
    />
  );

  const renderTrueFalse = () => (
    <div className="space-y-2">
      {['True', 'False'].map((option) => (
        <label key={option} className="block cursor-pointer">
          <input
            type="radio"
            name={`question-${question.id}`}
            value={option}
            checked={currentAnswer === option}
            onChange={(e) => onAnswerChange(question.id, e.target.value)}
            className="mr-2"
          />
          {option}
        </label>
      ))}
    </div>
  );

  const renderCode = () => (
    <div>
      {question.expected_input && question.expected_output && (
        <div className="mb-4 p-3 bg-gray-100 rounded">
          <p className="font-semibold">Expected Input:</p>
          <pre className="bg-white p-2 rounded border">{question.expected_input}</pre>
          <p className="font-semibold mt-2">Expected Output:</p>
          <pre className="bg-white p-2 rounded border">{question.expected_output}</pre>
        </div>
      )}

      <CodeEditor
        code={currentAnswer || ''}
        onCodeChange={(code) => onAnswerChange(question.id, code)}
      />

      <div className="mt-3 flex gap-3">
        <button
          onClick={() => runCode('run')}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:opacity-50"
          disabled={loading}
        >
          Run
        </button>
        <button
          onClick={() => runCode('test')}
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 disabled:opacity-50"
          disabled={loading}
        >
          Test
        </button>
      </div>

      {runOutput && (
        <div className="mt-3 p-3 bg-black text-white rounded">
          <pre>{runOutput}</pre>
        </div>
      )}
    </div>
  );

  const renderQuestionInput = () => {
    switch (question.question_type) {
      case 'mcq':
        return renderMCQ();
      case 'text':
        return renderText();
      case 'tf':
        return renderTrueFalse();
      case 'code':
        return renderCode();
      default:
        return <p>Unsupported question type: {question.question_type}</p>;
    }
  };

  return (
    <div className="card mb-6 p-4 border rounded shadow">
      <h3 className="text-xl font-semibold mb-4">{question.question_text}</h3>
      {renderQuestionInput()}
    </div>
  );
};

export default Question;
