import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { useAuth } from '../contexts/AuthContext';
import { toast } from 'react-toastify';
import axios from 'axios';

const Dashboard = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { authToken } = useAuth();

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const token = localStorage.getItem('access');
        const response = await axios.get('http://localhost:8000/api/quizzes/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setQuizzes(response.data);
      } catch (err) {
        console.error('Failed to fetch quizzes:', err);
        setError(err);
        toast.error('Failed to load quizzes. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (authToken) {
      fetchQuizzes();
    }
  }, [authToken]);

  const handleStartQuiz = async (quizId) => {
    try {
      const token = localStorage.getItem('access'); // your JWT token
      await axios.post(
        `http://localhost:8000/api/quizzes/${quizId}/start/`,
        {}, // empty body unless you want to send extra data
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      toast.success('Quiz started successfully!');
      setTimeout(() => {
        window.location.href = `/quiz/${quizId}`;
      }, 500);
    } catch (err) {
      console.error('Failed to start quiz:', err);
      const errorMessage =
        err.response?.data?.error || 'Failed to start quiz. Please try again.';
      toast.error(errorMessage);
    }
  };

  if (loading) {
    return <Loader />;
  }

  if (error) {
    return <ErrorMessage message="Failed to load quizzes." />;
  }

  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Available Quizzes</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {quizzes.map((quiz) => (
          <div key={quiz.id} className="card">
            <h3 className="text-xl font-semibold mb-2">{quiz.title}</h3>
            <p className="text-gray-600 mb-4">{quiz.description}</p>
            <div className="flex justify-between items-center text-sm text-gray-500 mb-4">
              <span>Duration: {quiz.duration_minutes} minutes</span>
            </div>
            <button
              onClick={() => handleStartQuiz(quiz.id)}
              className="btn btn-primary w-full"
            >
              Start Quiz
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
