// src/components/common/Loader.jsx
import React from 'react';

/**
 * Loader component showing a spinning indicator with a loading text.
 * Used to indicate ongoing asynchronous operations or page loads.
 */
const Loader = () => {
  return (
    <div className="flex justify-center items-center h-full my-12">
      {/* Spinning circle */}
      <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-indigo-500"></div>
      {/* Loading text */}
      <p className="ml-4 text-xl text-gray-600">Loading...</p>
    </div>
  );
};

export default Loader;
