import React, { useState } from 'react';
import CodeEditor from './CodeEditor';

const Question = ({ question, onAnswerChange, currentAnswer }) => {
  const [runOutput, setRunOutput] = useState('');

  const handleRun = () => {
    try {
      // Simple JS simulation (for Python, call backend API)
      if (currentAnswer) {
        // Send code to backend or simulate execution
        setRunOutput('Code sent for execution...');
      } else {
        setRunOutput('No code provided to run.');
      }
    } catch (err) {
      setRunOutput(`Error: ${err.message}`);
    }
  };

  const handleTest = () => {
    setRunOutput('Testing code against sample cases...');
  };

  const renderQuestionInput = () => {
    switch (question.question_type) {
      case 'mcq':
        return (
          <div className="space-y-4">
            {question.choices?.map((choice) => (
              <label key={choice.id} className="block cursor-pointer">
                <input
                  type="radio"
                  name={`question-${question.id}`}
                  value={choice.choice_text}
                  checked={currentAnswer === choice.choice_text}
                  onChange={(e) => onAnswerChange(question.id, e.target.value)}
                  className="mr-2"
                />
                {choice.choice_text}
              </label>
            ))}
          </div>
        );

      case 'text':
        return (
          <input
            type="text"
            className="form-input w-full"
            value={currentAnswer || ''}
            onChange={(e) => onAnswerChange(question.id, e.target.value)}
            placeholder="Type your answer here..."
          />
        );

      case 'tf':
        return (
          <div className="space-y-2">
            {['True', 'False'].map((option) => (
              <label key={option} className="block cursor-pointer">
                <input
                  type="radio"
                  name={`question-${question.id}`}
                  value={option}
                  checked={currentAnswer === option}
                  onChange={(e) => onAnswerChange(question.id, e.target.value)}
                  className="mr-2"
                />
                {option}
              </label>
            ))}
          </div>
        );

      case 'code':
        return (
          <div>
            {question.expected_input && question.expected_output && (
              <div className="mb-4 p-3 bg-gray-100 rounded">
                <p className="font-semibold">Expected Input:</p>
                <pre className="bg-white p-2 rounded border">{question.expected_input}</pre>
                <p className="font-semibold mt-2">Expected Output:</p>
                <pre className="bg-white p-2 rounded border">{question.expected_output}</pre>
              </div>
            )}

            <CodeEditor
              code={currentAnswer || ''}
              onCodeChange={(code) => onAnswerChange(question.id, code)}
            />

            <div className="mt-3 flex gap-3">
              <button
                onClick={handleRun}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Run
              </button>
              <button
                onClick={handleTest}
                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
              >
                Test
              </button>
            </div>

            {runOutput && (
              <div className="mt-3 p-3 bg-black text-white rounded">
                <pre>{runOutput}</pre>
              </div>
            )}
          </div>
        );

      default:
        return <p>Unsupported question type: {question.question_type}</p>;
    }
  };

  return (
    <div className="card mb-6 p-4 border rounded shadow">
      <h3 className="text-xl font-semibold mb-4">{question.question_text}</h3>
      {renderQuestionInput()}
    </div>
  );
};

export default Question;
