import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import Question from '../components/quiz/Question';
import Timer from '../components/quiz/Timer';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import { toast } from 'react-toastify';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';

const QuizPage = () => {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const attemptId = searchParams.get('attempt');
  const navigate = useNavigate();
  const { authToken } = useAuth();

  const [answers, setAnswers] = useState({});
  const [quizDetails, setQuizDetails] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [alreadySubmitted, setAlreadySubmitted] = useState(false);

  useEffect(() => {
    if (!id || !attemptId) {
      setError('Quiz ID or Attempt ID missing from URL.');
      setLoading(false);
      return;
    }

    if (!authToken) {
      setError('Not authenticated. Please log in.');
      setLoading(false);
      return;
    }

    const fetchAttemptData = async () => {
      try {
        const attemptRes = await api.get(`/api/student-quizzes/${attemptId}/`);
        const data = attemptRes.data;

        if (data.submitted) {
          setAlreadySubmitted(true);
        }

        setQuizDetails({
          title: data.quiz_title || data.quiz?.title || 'Quiz',
          description: data.quiz_description || data.quiz?.description || '',
          duration_minutes: data.duration_minutes ?? data.quiz?.duration_minutes ?? 60,
        });

        setQuestions(Array.isArray(data.questions) ? data.questions : []);
      } catch (err) {
        console.error('Error fetching quiz attempt:', err);
        const message = err.response?.data?.detail || err.message || 'Failed to load quiz.';
        setError(message);
        toast.error(message);
      } finally {
        setLoading(false);
      }
    };

    fetchAttemptData();
  }, [id, attemptId, authToken]);

  const handleAnswerChange = (questionId, answer) => {
    setAnswers((prev) => ({ ...prev, [String(questionId)]: answer }));
  };

  const handleSubmit = async () => {
    if (alreadySubmitted) {
      toast.info('You have already submitted this quiz.');
      return;
    }

    if (questions.length === 0) {
      toast.error('No questions to submit.');
      return;
    }

    const answersPayload = questions
      .map((q) => {
        const qid = String(q.id);
        const ans = answers[qid] ?? '';
        return {
          question: Number(qid),
          selected_option: typeof ans === 'number' ? ans : Number(ans) || null
        };
      })
      .filter((a) => a.selected_option !== null && a.selected_option !== '');

    if (answersPayload.length === 0) {
      toast.error('Please answer at least one question.');
      return;
    }

    try {
      setIsSubmitting(true);
      const res = await api.post(`/api/student-quizzes/${attemptId}/submit/`, {
        answers: answersPayload
      });
      toast.success('Quiz submitted successfully!');
      setAlreadySubmitted(true);
      navigate(`/results/${attemptId}`, { state: { result: res.data } });
    } catch (err) {
      console.error('Error submitting quiz:', err);
      const message =
        err.response?.data?.error ||
        err.response?.data?.detail ||
        err.message ||
        'Submission failed. Please try again.';
      toast.error(message);
      if (message.includes('already been submitted')) {
        setAlreadySubmitted(true);
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="container mx-auto mt-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-gray-800">{quizDetails?.title}</h2>
        <Timer
          durationMinutes={quizDetails?.duration_minutes}
          onTimeUp={() => {
            if (!alreadySubmitted) {
              handleSubmit();
            }
          }}
        />
      </div>

      <div className="space-y-6">
        {questions.length > 0 ? (
          questions.map((question) => (
            <Question
              key={question.id}
              question={question}
              onAnswerChange={handleAnswerChange}
              currentAnswer={answers[String(question.id)]}
              disabled={alreadySubmitted}
            />
          ))
        ) : (
          <p className="text-gray-500">No questions available for this quiz.</p>
        )}
      </div>

      <button
        onClick={handleSubmit}
        disabled={isSubmitting || alreadySubmitted}
        className={`btn w-full mt-8 py-3 rounded-lg transition-colors ${
          alreadySubmitted
            ? 'bg-gray-400 cursor-not-allowed'
            : 'bg-blue-600 hover:bg-blue-700 text-white'
        }`}
        aria-label="Submit Quiz"
      >
        {alreadySubmitted
          ? 'Quiz Already Submitted'
          : isSubmitting
          ? 'Submitting...'
          : 'Submit Quiz'}
      </button>
    </div>
  );
};

export default QuizPage;
