import React from 'react';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import useApi from '../hooks/useApi';
import { FaCheckCircle, FaTimesCircle, FaClock, FaTrophy, FaArrowLeft } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const ResultsPage = () => {
  const { data: results, loading, error } = useApi('student-quizzes/');
  const navigate = useNavigate();

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message="Failed to load quiz results." />;

  const getFeedback = (scorePercent) => {
    if (scorePercent >= 80) return 'Excellent work! ðŸŽ¯';
    if (scorePercent >= 50) return 'Good job! Keep practicing. ðŸ’ª';
    return "Don't give up! Try again. ðŸš€";
  };

  return (
    <div className="container mx-auto mt-8 px-4">
      <h2 className="text-3xl font-bold mb-6 text-indigo-800 text-center">My Quiz Results</h2>
      <div className="space-y-6 max-w-3xl mx-auto">
        {Array.isArray(results) && results.length > 0 ? (
          results.map((result) => {
            const attempted = result.attempted ?? 0;
            const total = result.total_questions ?? 0;
            const unattempted = result.unattempted ?? Math.max(total - attempted, 0);
            const score = result.total_score ?? 0;
            const totalPoints = result.total_possible_points ?? total;
            const scorePercent = totalPoints > 0 ? Math.round((score / totalPoints) * 100) : 0;

            // Time taken formatting
            const timeTakenSeconds = Number(result.time_taken ?? 0);
            const minutes = Math.floor(timeTakenSeconds / 60);
            const seconds = Math.floor(timeTakenSeconds % 60);
            const timeTaken = total > 0 ? `${minutes}m ${seconds}s` : 'â€”';

            return (
              <div
                key={result.id}
                className="bg-white shadow-lg rounded-xl p-6 border border-gray-200 hover:shadow-2xl transition duration-300"
              >
                <h3 className="text-2xl font-semibold mb-4 text-gray-800">
                  {result.quiz?.title ?? result.quiz_title ?? 'Untitled Quiz'}
                </h3>

                <p className="text-gray-500 mb-2">
                  Completed on:{' '}
                  {result.completed_at
                    ? new Date(result.completed_at).toLocaleString()
                    : 'â€”'}
                </p>

                {/* Progress Bar */}
                <div className="mb-4">
                  <div className="w-full bg-gray-200 rounded-full h-4">
                    <div
                      className={`h-4 rounded-full ${
                        scorePercent >= 80
                          ? 'bg-green-500'
                          : scorePercent >= 50
                          ? 'bg-yellow-500'
                          : 'bg-red-500'
                      }`}
                      style={{ width: `${scorePercent}%` }}
                    />
                  </div>
                  <p className="text-sm text-gray-600 mt-1">{scorePercent}% Score</p>
                </div>

                {/* Stats */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
                  <div>
                    <FaTrophy className="text-yellow-500 text-2xl mx-auto mb-1" />
                    <p className="font-semibold">{score} Points</p>
                  </div>
                  <div>
                    <FaCheckCircle className="text-green-500 text-2xl mx-auto mb-1" />
                    <p className="font-semibold">{attempted} Attempted</p>
                  </div>
                  <div>
                    <FaTimesCircle className="text-red-500 text-2xl mx-auto mb-1" />
                    <p className="font-semibold">{unattempted} Unattempted</p>
                  </div>
                  <div>
                    <FaClock className="text-blue-500 text-2xl mx-auto mb-1" />
                    <p className="font-semibold">{timeTaken}</p>
                  </div>
                </div>

                {/* Feedback */}
                <div className="mt-4 text-center">
                  <p
                    className={`text-lg font-bold ${
                      scorePercent >= 80
                        ? 'text-green-600'
                        : scorePercent >= 50
                        ? 'text-yellow-600'
                        : 'text-red-600'
                    }`}
                  >
                    {getFeedback(scorePercent)}
                  </p>
                </div>

                {/* Back to Dashboard */}
                <div className="mt-6 text-center">
                  <button
                    onClick={() => navigate('/dashboard')}
                    className="flex items-center justify-center gap-2 px-6 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-full shadow-md transition duration-300"
                    aria-label="Back to Dashboard"
                  >
                    <FaArrowLeft /> Back to Dashboard
                  </button>
                </div>
              </div>
            );
          })
        ) : (
          <p className="text-gray-500 text-center">No results yet.</p>
        )}
      </div>
    </div>
  );
};

export default ResultsPage;
