import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const LoginForm = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await login(form.username, form.password);
      navigate('/dashboard');
    } catch (err) {
      setError(err.message || 'Login failed.');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-12 p-6 border rounded shadow-md">
      <h2 className="text-3xl font-semibold mb-6 text-center text-indigo-700">Login to Quiz Website</h2>
      {error && <p className="text-red-600 mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label className="block mb-2 font-medium" htmlFor="username">Username</label>
        <input
          id="username"
          name="username"
          type="text"
          className="w-full p-2 border rounded mb-4"
          value={form.username}
          onChange={handleChange}
          required
          autoFocus
        />
        <label className="block mb-2 font-medium" htmlFor="password">Password</label>
        <input
          id="password"
          name="password"
          type="password"
          className="w-full p-2 border rounded mb-4"
          value={form.password}
          onChange={handleChange}
          required
        />
        <button
          type="submit"
          className="w-full bg-indigo-700 text-white py-2 rounded hover:bg-indigo-800 transition"
        >
          Login
        </button>
      </form>
      <p className="mt-4 text-center">
        Don't have an account?{' '}
        <Link to="/signup" className="text-indigo-700 hover:underline">Sign up</Link>
      </p>
      <p className="mt-2 text-center">
        <Link to="/forgot-password" className="text-indigo-600 hover:underline">Forgot password?</Link>
      </p>
    </div>
  );
};

export default LoginForm;
