// src/services/api.js
import axios from 'axios';

// Automatically switch between dev and production URLs
const API_BASE_URL =
  process.env.NODE_ENV === 'development'
    ? 'http://localhost:8000/api/' // Django backend in dev
    : 'https://your-production-backend.com/api/'; // Change to your production backend URL

// Create an instance of axios with a base URL
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true, // Needed for CSRF/session cookies if used
});

// Request interceptor to add the authentication token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Token ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      console.warn('Authentication token expired or invalid. Please log in again.');
      // Optional: auto logout
      // localStorage.removeItem('authToken');
      // window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
