import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Header from './components/shared/Header';
import Footer from './components/shared/Footer';
import LoginForm from './components/user/LoginForm';
import SignupForm from './components/user/SignupForm';
import Dashboard from './pages/Dashboard';
import QuizPage from './pages/QuizPage';
import ResultsPage from './pages/ResultsPage';
import Leaderboard from './pages/Leaderboard';
import { useAuth } from './contexts/AuthContext';

// The PrivateRoute component is defined here to protect pages that require authentication.
const PrivateRoute = ({ children }) => {
  const { isLoggedIn } = useAuth();
  // If the user is logged in, render the child components. Otherwise, redirect to the login page.
  return isLoggedIn ? children : <Navigate to="/login" />;
};

const App = () => {
  const { isLoggedIn, logout } = useAuth();

  return (
    <div className="flex flex-col min-h-screen">
      {/* The Header is included at the top, passing the login state and logout function. */}
      <Header isLoggedIn={isLoggedIn} onLogout={logout} />
      <main className="flex-grow">
        <Routes>
          <Route path="/login" element={<LoginForm />} />
          <Route path="/signup" element={<SignupForm />} />
          <Route
            path="/"
            element={
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            }
          />
          <Route
            path="/quiz/:id"
            element={
              <PrivateRoute>
                <QuizPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/results"
            element={
              <PrivateRoute>
                <ResultsPage />
              </PrivateRoute>
            }
          />
          <Route
            path="/leaderboard"
            element={
              <PrivateRoute>
                <Leaderboard />
              </PrivateRoute>
            }
          />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </main>
      {/* The Footer is at the bottom. */}
      <Footer />
      {/* The ToastContainer is rendered here to make sure toasts are available globally. */}
      <ToastContainer position="bottom-right" />
    </div>
  );
};

export default App;
