import React, { useState } from 'react';
import api from '../../services/api'; // axios instance with baseURL
import { useAuth } from '../../contexts/AuthContext';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';  // import jwt-decode

const LoginForm = () => {
  const { setAuthToken, setUserId, setUserEmail } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // POST to JWT token endpoint
      const response = await api.post('/users/token/', {
        username: email,  // backend expects 'username', sending email here if that's your login field
        password: password,
      });

      const { access, refresh } = response.data;

      // Decode JWT access token to get user info (assuming these claims exist)
      const decoded = jwt_decode(access);
      // For example, if you put user id/email in token payload as 'user_id' and 'email'
      const userId = decoded.user_id || null;
      const userEmail = decoded.email || email; // fallback to entered email if no claim

      // Save tokens and user info in localStorage
      localStorage.setItem('authToken', access);
      localStorage.setItem('refreshToken', refresh);
      localStorage.setItem('userId', userId);
      localStorage.setItem('userEmail', userEmail);

      // Update context state
      setAuthToken(access);
      setUserId(userId);
      setUserEmail(userEmail);

      toast.success('Login successful!');
      navigate('/dashboard');
    } catch (error) {
      console.error('Login failed:', error.response?.data || error.message);
      toast.error(error.response?.data?.detail || 'Login failed!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="login-form">
      <div>
        <label>Email</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
        />
      </div>

      <div>
        <label>Password</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
        />
      </div>

      <button type="submit" disabled={loading}>
        {loading ? 'Logging in...' : 'Login'}
      </button>
    </form>
  );
};

export default LoginForm;
