import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Loader from '../components/common/Loader';
import ErrorMessage from '../components/common/ErrorMessage';
import api from '../services/api';

const ResultsDetailPage = () => {
  const { id } = useParams();
  const [attempt, setAttempt] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // API path with /quizzes/ prefix to match backend routing
    api.get(`/quizzes/student-quizzes/${id}/`)
      .then((res) => setAttempt(res.data))
      .catch(() => setError('Failed to load result details.'))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <Loader />;
  if (error) return <ErrorMessage message={error} />;
  if (!attempt) return null;

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-semibold mb-6 text-indigo-700">
        Results for {attempt.quiz.title}
      </h1>
      <p className="mb-4">
        Total Score: {attempt.total_score.toFixed(2)} / {attempt.total_possible_points}
      </p>
      <ol className="list-decimal ml-6 space-y-4">
        {attempt.answers.map(({ question, selected_option, answer_text }, idx) => (
          <li key={question.id || idx}>
            <div className="mb-1 font-semibold">{question.text}</div>
            <div>
              <strong>Your Answer:</strong>{' '}
              {question.question_type === 'text'
                ? answer_text || 'No answer'
                : Array.isArray(selected_option)
                  ? selected_option.join(', ')
                  : String(selected_option)}
            </div>
            {/* Optionally show correctness and expected answer if available */}
          </li>
        ))}
      </ol>
    </div>
  );
};

export default ResultsDetailPage;
