import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:8000/api/";

const api = axios.create({
  baseURL: API_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// Request Interceptor – Attach token from localStorage
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token"); // consistent key usage

    if (token) {
      // Use DRF TokenAuthentication header format
      config.headers.Authorization = `Token ${token}`;

      // If using JWT instead, comment above and uncomment below:
      // config.headers.Authorization = `Bearer ${token}`;
    }

    return config;
  },
  (error) => Promise.reject(error)
);

// Response Interceptor – Handle unauthorized & forbidden globally
api.interceptors.response.use(
  (response) => response,
  (error) => {
    const status = error.response?.status;

    if (status === 401) {
      console.warn("Unauthorized: Redirecting to login...");
      localStorage.removeItem("token");
      localStorage.removeItem("userId");
      localStorage.removeItem("userEmail");
      window.location.href = "/login";
    } else if (status === 403) {
      console.warn("Forbidden: You do not have permission to perform this action.");
      // Optional: Add UI notification here
    }

    return Promise.reject(error);
  }
);

export default api;
