import React from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isLoggedIn, onLogout }) => {
  return (
    <header className="bg-white shadow-sm sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <Link to="/" className="flex items-center space-x-2">
          {/* SVG code for a simple logo is embedded here */}
          <svg className="h-8 w-8 text-indigo-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6.253v13m0-13C10.838 5.438 9.294 5 7.75 5c-2.457 0-4.702 1.353-5.592 3.513l-.58 1.454c-.394.984-.114 2.16.712 2.89l.526.467c.33.293.498.718.498 1.157v3.256m0-13C13.162 5.438 14.706 5 16.25 5c2.457 0 4.702 1.353 5.592 3.513l.58 1.454c.394.984.114 2.16.712 2.89l.526.467c.33.293.498.718.498 1.157v3.256M12 6.253v13" />
          </svg>
          <span className="text-xl font-bold text-gray-800">B.Tech Exams</span>
        </Link>
        <nav className="hidden md:flex space-x-8 items-center">
          <Link to="/" className="text-gray-600 hover:text-indigo-600 transition-colors duration-200">
            Dashboard
          </Link>
          <Link to="/results" className="text-gray-600 hover:text-indigo-600 transition-colors duration-200">
            My Results
          </Link>
          <Link to="/leaderboard" className="text-gray-600 hover:text-indigo-600 transition-colors duration-200">
            Leaderboard
          </Link>
          {isLoggedIn ? (
            <button onClick={onLogout} className="btn btn-primary">
              Logout
            </button>
          ) : (
            <Link to="/login" className="btn btn-primary">
              Login
            </Link>
          )}
        </nav>
        {/* Mobile menu button will go here */}
      </div>
    </header>
  );
};

export default Header;
