// src/components/common/Timer.jsx
import React, { useState, useEffect } from 'react';

/**
 * Timer component counts down from given duration (in minutes) to zero.
 * Calls onTimeUp callback when time reaches zero.
 * 
 * Props:
 * - durationMinutes: number (duration in minutes)
 * - onTimeUp: function callback when time runs out
 */
const Timer = ({ durationMinutes, onTimeUp }) => {
  const [timeLeft, setTimeLeft] = useState(durationMinutes * 60);

  useEffect(() => {
    if (timeLeft <= 0) {
      onTimeUp();
      return;
    }

    // Decrement time every second
    const timerId = setInterval(() => {
      setTimeLeft(prevTime => prevTime - 1);
    }, 1000);

    return () => clearInterval(timerId);
  }, [timeLeft, onTimeUp]); // runs on timeLeft or onTimeUp changes

  // Format seconds to MM:SS string
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="text-center text-lg font-bold text-gray-800 p-4 rounded-md bg-white shadow-md">
      Time Left: <span className="text-red-500">{formatTime(timeLeft)}</span>
    </div>
  );
};

export default Timer;
